${
if(param("id")==null||param("id")==""){
  return "select gcl.* "
+",CASE gcl.status WHEN 10 THEN \'新建\' WHEN 20 THEN \'综合审核\' WHEN 30 THEN \'施工负责人审核\'  "
+"WHEN 40 THEN \'经营科审核\' WHEN 50 THEN \'总负责人审核\' WHEN 60 THEN \'财务负责人审核\' WHEN 70 THEN \'已完成\' ELSE \'未知\' END  shzt "
+"from tbl_measure_volume gcl "	
+" where 1=1"
	}else{
 return "select gcl.* "
+",CASE gcl.status WHEN 10 THEN \'新建\' WHEN 20 THEN \'综合审核\' WHEN 30 THEN \'施工负责人审核\'  "
+"WHEN 40 THEN \'经营科审核\' WHEN 50 THEN \'总负责人审核\' WHEN 60 THEN \'财务负责人审核\' WHEN 70 THEN \'已完成\' ELSE \'未知\' END  shzt "
+"from tbl_measure_volume gcl "	
+" where 1=1"
      +" and gcl.id=:id"
   }
}

